<?php	class classes{       public function getRow($table, $line, $referencia, $id){			global $con;		    $sql = "SELECT $line from $table WHERE $referencia = $id";			$query = mysqli_query($con, $sql);			$result = mysqli_fetch_assoc($query);	        return $result[$line];	     }			 		 public function getAderencia($id){			global $con;		    $sql = "SELECT  `id_aluno`, `id_zona`, `id_percurso`, `id_carrinha`, `id_educadora` FROM `tbladerencia` WHERE `id_aderencia` = $id";			$query = mysqli_query($con, $sql);			$result = mysqli_fetch_assoc($query);	        return $result;	     }	       	     public function getEs($id){			global $con;		    $sql = "SELECT `id_estado`, `nome_estado` FROM `tblestado` WHERE id_estado = $id";			$query = mysqli_query($con, $sql);			$result = mysqli_fetch_assoc($query);	        return $result;	     }			 		  public function getTpresenca($id){			global $con;		    $sql = "SELECT `id_tpresenca`, `nome_tpresenca` FROM `tbltpresenca` WHERE id_tpresenca = $id";			$query = mysqli_query($con, $sql);			$result = mysqli_fetch_assoc($query);	        return $result;	     }			 		  public function getEstadoPresenca($id){			global $con;		    $sql = "SELECT `id_estadopresenca`, `nome_estadopresenca` FROM `tblestadopresenca` WHERE id_estadopresenca = $id";			$query = mysqli_query($con, $sql);			$result = mysqli_fetch_assoc($query);	        return $result;	     }	}?>   		 